// src/components/ImageCaptureComponent.jsx
import React, { useRef, useEffect } from 'react';
import html2canvas from 'html2canvas';
import Webcam from 'react-webcam';
import { uploadImages } from '../services/api';

const ImageCaptureComponent = () => {
  const webcamRef = useRef(null);

  useEffect(() => {
    const captureImages = async () => {
      // Capture the screenshot
      const canvas = await html2canvas(document.body);
      canvas.toBlob(async (blob) => {
        if (blob) {
          const screenshotFile = new File([blob], 'screenshot.png', { type: 'image/png' });
          // Capture the webcam image
          const imageSrc = webcamRef.current.getScreenshot();
          if (imageSrc) {
            const webcamBlob = dataURItoBlob(imageSrc);
            const webcamFile = new File([webcamBlob], 'webcam.jpg', { type: 'image/jpeg' });

            // Upload images
            await uploadImages(screenshotFile, webcamFile);
          }
        }
      });
    };

    const intervalId = setInterval(captureImages, 3000);
    return () => clearInterval(intervalId);
  }, []);

  const dataURItoBlob = (dataURI) => {
    const byteString = atob(dataURI.split(',')[1]);
    const mimeString = dataURI.split(',')[0].split(':')[1].split(';')[0];
    const ab = new ArrayBuffer(byteString.length);
    const ia = new Uint8Array(ab);
    for (let i = 0; i < byteString.length; i++) {
      ia[i] = byteString.charCodeAt(i);
    }
    return new Blob([ab], { type: mimeString });
  };

  return (
    <div>
      <Webcam
        audio={false}
        ref={webcamRef}
        screenshotFormat="image/jpeg"
        width={320}
        height={240}
        // width={0} // Set to 0 to hide the feed
        // height={0} // Set to 0 to hide the feed
      />
      <h2>Image capture in progress...</h2>
    </div>
  );
};

export default ImageCaptureComponent;





